import React, { useEffect, useState } from "react";
import { Formik, Form } from "formik";
import "./styles.css";
import InputField from "../inputField/InputField";
import SelectField from "../selectField/SelectField";
import { useGetFilmsMutation } from "../../redux/filmApi";
import { object } from "yup";

const FormComponent = () => {
  const [films, setFilms] = useState(null);
  const [getFilms] = useGetFilmsMutation();
  const filmSchema = object({ firstName: "", lastName: "", favoriteMovie: "" });

  const getFilmNames = async () => {
    const {
      data: {
        data: {
          allFilms: { films },
        },
      },
    } = await getFilms();
    setFilms(films);
  };

  useEffect(() => {
    getFilmNames();
  }, []);

  const handleFormSubmit = async (values) => {
    console.log("Submited===>", values);
  };

  return (
    <div className="form-container">
      <h3 className="title">My form</h3>
      {films && (
        <Formik
          initialValues={{
            firstName: "",
            lastName: "",
            favoriteMovie: films[0]?.title,
          }}
          onSubmit={handleFormSubmit}
        >
          <Form className="form">
            <InputField fieldName={"First Name"} htmlName={"firstName"} />
            <InputField fieldName={"Last Name"} htmlName={"lastName"} />

            <SelectField
              fieldName={"Favorite Star Wars movie"}
              htmlName={"favoriteMovie"}
              data={films}
            />
            <button type="submit" className="submit-button">
              Submit
            </button>
          </Form>
        </Formik>
      )}
    </div>
  );
};

export default FormComponent;
