import React, { useEffect, useState } from "react";
import { Formik, Form } from "formik";
import "./styles.css";
import InputField from "../inputField/InputField";
import SelectField from "../selectField/SelectField";
import { useGetFilmsMutation } from "../../redux/filmApi";
import { object, string } from "yup";

const FormComponent = () => {
  const [films, setFilms] = useState(null);
  const [getFilms] = useGetFilmsMutation();
  const [errors, setErrors] = useState([]);
  const filmSchema = object({
    firstName: string()
      .trim()
      .required("You need to enter a first name", { abortEarly: false }),
    lastName: string()
      .trim()
      .required("You need to enter a last name", { abortEarly: false }),
    favoriteMovie: string().required("You need to select a movie", {
      abortEarly: false,
    }),
  });

  const getFilmNames = async () => {
    const {
      data: {
        data: {
          allFilms: { films },
        },
      },
    } = await getFilms();
    setFilms(films);
  };

  useEffect(() => {
    getFilmNames();
  }, []);

  const handleFormSubmit = async (values) => {
    try {
      const validation = filmSchema.validateSync(values);
      console.log("Validate===>", validation);
    } catch (error) {
      console.log("Error====>", error);
      setErrors([...errors, error.message]);
    }
  };

  return (
    <div className="form-container">
      <h3 className="title">My form</h3>
      <div className="error-container">
        {errors.map((error, index) => (
          <p key={error + index} className="error-txt">
            {error}
          </p>
        ))}
      </div>
      {films && (
        <Formik
          initialValues={{
            firstName: "",
            lastName: "",
            favoriteMovie: films[0]?.title,
          }}
          onSubmit={handleFormSubmit}
        >
          <Form className="form">
            <InputField fieldName={"First Name"} htmlName={"firstName"} />
            <InputField fieldName={"Last Name"} htmlName={"lastName"} />

            <SelectField
              fieldName={"Favorite Star Wars movie"}
              htmlName={"favoriteMovie"}
              data={films}
            />
            <button type="submit" className="submit-button">
              Submit
            </button>
          </Form>
        </Formik>
      )}
    </div>
  );
};

export default FormComponent;
