import React, { useEffect } from "react";
import { Field } from "formik";
import "./styles.css";
import { useGetFilmsMutation } from "../../redux/filmApi";

const SelectField = ({ htmlName, fieldName }) => {
  const [getFilms] = useGetFilmsMutation();
  const getFilmNames = async () => {
    const {
      data: {
        data: {
          allFilms: { films },
        },
      },
    } = await getFilms();
    console.log(films);
  };
  useEffect(() => {
    getFilmNames();
  }, []);

  return (
    <div className="select-container">
      <label htmlFor={htmlName} className="label">
        {fieldName} <span className="asteric">*</span>
      </label>
      <Field as="select" name={htmlName} className="select-field" required>
        <option value="red">Red</option>
        <option value="green">Green</option>
        <option value="blue">Blue</option>
      </Field>
    </div>
  );
};

export default SelectField;
